package String;

public class ReplaceMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        String s1="Harry";
        s1=s1.replace('r', 'p');
        System.out.println(s1);
        String s2="aaa bbb ccc";
        s2=s2.replace(" ", "");
        System.out.println(s2);
        String s3="Sayalii";
        s3=s3.replace('i', 'e');
        System.out.println(s3);
        }
}