package String;

public class StartsWithEndsWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       String s1="Sayalee";
       System.out.println(s1.startsWith("S"));
       System.out.println(s1.endsWith("e"));
       System.out.println(s1.startsWith("Sa"));
       System.out.println(s1.startsWith("r"));
       System.out.println(s1.endsWith("e"));
       System.out.println(s1.endsWith("ee"));
       System.out.println(s1.startsWith("sa"));
       
	}
 
}
