package Unboxing;

public class UnBoxing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        Integer a=21;
        int b=a.intValue();
        System.out.println(b);
        
        Double a1=21.5;
        double b1=a1.doubleValue();
        System.out.println(b1);
	}

}
