package WrapperClass;

public class AutoBoxing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		Integer b=a;
		System.out.println(b);
		
		double a1=10.9;
		Double b1=a1;
		System.out.println(b1);
		
		boolean a2=true;
		Boolean b2=new Boolean(a2);
		System.out.println(b2);
	}

}
